var history = new Array(10);

exports.add = function (a, b)
{
    var c = a + b;
    history.push( a + " + " + b + " = " + c );
    return c;
}
exports.subtract = function(a, b)
{
    var c = a - b;
    history.push( a + " - " + b + " = " + c);
    return c;
}
exports.multiply = function (a, b)
{
    var c = a * b;
    history.push( a + " * " + b + " = " + c);
    return c;
}
exports.divide = function(a, b)
{
    var c = undefined;
    if( b > 0 )
    {
        c = a / b;
        history.push( a + " / " + b + " = " + c );
    }
    return c;
}
exports.sqrt = function ( a )
{
    var c = Math.sqrt(a);
    history.push( "Math.sqrt( " + a + " ) " + " = " + c );
    return c;
}
exports.history = function ()
{
    console.log( "\nHistory:" );
    
    var lengthToRemove = history.length > 10 ? history.length - 10 : 0;
    history.splice(0, lengthToRemove);

    for( var i=0; i < history.length; i++ )
    {
        console.log( "History " + String(i+1) + ": "+ history[i] );
    }
}